let diffSummary = '';

for (const item of $input.all()) {
  const { filename, sha, patch } = item.json;

  diffSummary += `File: ${filename}\n`;
  diffSummary += `Commit ID: ${sha}\n`;

  if (patch) { 
    diffSummary += `Patch: ${patch}\n\n`;
  } else {
    diffSummary += `Patch not available.\n\n`;
  }
}

return [
  {
    json: {
      diff_summary: diffSummary
    }
  }
];

